import axios from "axios";
import {useState, useEffect} from "react";
import Coin from "./Coin";
import './App.css'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

function App() {

  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState('');

  useEffect( () => {
    axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=cad&order=market_cap_desc&per_page=100&page=1&sparkline=false
    `) // promise 
    .then(res => {
      setCoins(res.data)
      console.log(res.data)
    }).catch(error => alert("error!"))
  }, []);

  const handleChange = e => {
    setSearch(e.target.value)
    console.log(coins)
  }

  function filterCoins(coin) {
    return coin.name.toLowerCase().includes(search.toLowerCase())
  }

  const filteredCoins = coins.filter(filterCoins);

  return (
    <div className="App">
      <div className="coin-search">
        <h1 className="coin-text">Search a currency</h1>
        <form>
          <input onChange={handleChange} type="text" placeholder="Search" className="coin-input"/>
        </form>
      </div>
      <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
            <TableCell align="right">Fat&nbsp;(g)</TableCell>
            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
            <TableCell align="right">Protein&nbsp;(g)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredCoins.map((coin) => (
            <TableRow
              key={coin.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {coin.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
              <TableCell align="right">{row.carbs}</TableCell>
              <TableCell align="right">{row.protein}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
      {filteredCoins.map(coin => {
        return (
          <Coin 
          key={coin.id} 
          name={coin.name} 
          image={coin.image} 
          symbol={coin.symbol} 
          volume={coin.total_volume}
          price={coin.current_price} 
          priceChange={coin.price_change_percentage_24h}
          marketcap={coin.market_cap}
          />
        );
      })}
    </div>

  );
}

export default App;
