import axios from "axios";
import {useState, useEffect} from "react";
import Coin from "./Coin";
import './App.css'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

function App() {

  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState('');

  useEffect( () => {
    axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=cad&order=market_cap_desc&per_page=100&page=1&sparkline=false
    `) // promise 
    .then(res => {
      setCoins(res.data)
      console.log(res.data)
    }).catch(error => alert("error!"))
  }, []);

  const handleChange = e => {
    setSearch(e.target.value)
    console.log(coins)
  }

  function filterCoins(coin) {
    return coin.name.toLowerCase().includes(search.toLowerCase())
  }

  const filteredCoins = coins.filter(filterCoins);

  return (
    <div className="App">
      <div className="coin-search">
        <h1 className="coin-text">Search a currency</h1>
        <form>
          <input onChange={handleChange} type="text" placeholder="Search" className="coin-input"/>
        </form>
      </div>
      <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell className="title-text">Currency</TableCell>
            <TableCell align="right"></TableCell>
            <TableCell align="right">Symbol</TableCell>
            <TableCell align="right">Price</TableCell>
            <TableCell align="right">Volume</TableCell>
            <TableCell align="right">%</TableCell>
            <TableCell align="right">Mkt Cap</TableCell>      
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredCoins.map((coin) => (
            <TableRow
              key={coin.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 }}}
            >
            <TableCell><img src={coin.image} alt="crypto" /></TableCell>
            <TableCell><h1>{coin.name}</h1></TableCell>
            <TableCell align="right"><p className="coin-symbol">{coin.symbol}</p></TableCell>
            <TableCell align="right"><p className="coin-price">${coin.current_price}</p></TableCell>
            <TableCell align="right"><p className="coin-volume">${coin.total_volume.toLocaleString()}</p></TableCell>
            <TableCell align="right">{coin.price_change_percentage_24h < 0 ? (
                    <p className="coin-percent red">{coin.price_change_percentage_24h.toFixed(2)}%</p>
                ) : (<p className="coin-percent green">{coin.price_change_percentage_24h.toFixed(2)}%</p>)}</TableCell>
            <TableCell><p className="coin-marketcap">
                    ${coin.market_cap.toLocaleString()}
                </p></TableCell>

            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </div>

  );
}

export default App;
